(2309, 1, '2018-04-17 16:49:56', '2018-04-17 16:49:56', '<div class=\"left-column column\">\r\n<div class=\"content-wrapper\">\r\n<div class=\"left_par parsys twocolumnMedia\">\r\n<div class=\"copyblock parbase section\"><article class=\"copy-block \"><hgroup class=\"\">\r\n<h3 class=\"\">AudioShield technology</h3>\r\n</hgroup>\r\n<div class=\"richtext  \">\r\n\r\nListen with ease using active, noise-reducing AudioShield technology that delivers immersive audio while blocking out unwanted background noise.  Noise-reduction features are powered by a fifteen-hour rechargeable battery, meaning you can game for longer without interruptions.\r\n\r\n</div>\r\n</article></div>\r\n</div>\r\n</div>\r\n</div>\r\n<div class=\"right-column column\"></div>', 'PlayStation 4 Headphone', '<ul class=\"a-unordered-list a-vertical a-spacing-none\">\r\n 	<li><span class=\"a-list-item\">Specifically designed for the Playstation 4 system</span></li>\r\n 	<li><span class=\"a-list-item\">Always trust in Spinz® Brand</span></li>\r\n 	<li><span class=\"a-list-item\">Matches the colors of the Playstation 4</span></li>\r\n 	<li><span class=\"a-list-item\">Sound levels have been adjusted specifically for the Playstation 4</span></li>\r\n 	<li><span class=\"a-list-item\">Better than a PS4 Headset</span></li>\r\n</ul>', 'publish', 'open', 'closed', '', 'playstation-4-headphone', '', '', '2018-04-22 19:55:06', '2018-04-22 19:55:06', '', 0, 'http://localhost/FatherOfGaming/?post_type=product&#038;p=2309', 0, 'product', '', 0),
(2310, 1, '2018-04-17 16:54:43', '2018-04-17 16:54:43', '<div class=\"left-column column\">\r\n<div class=\"content-wrapper\">\r\n<div class=\"left_par parsys twocolumnMedia\">\r\n<div class=\"copyblock parbase section\"><article class=\"copy-block \"><hgroup class=\"\">\r\n<h3 class=\"\">See the future</h3>\r\n</hgroup>\r\n<div class=\"richtext \">\r\n\r\n<strong>5.7” OLED screen\r\n</strong>See hyper-real 3D environments come to life with a custom OLED screen.\r\n\r\n<strong>360 degree vision\r\n</strong>Whichever way you turn the 360 degree immersion of PS VR makes you part of a living, breathing world with a seamless field of view.\r\n\r\n<strong>120 frames per second\r\n</strong>Smooth visuals and super low latency combine to create an incredibly immersive gaming world.\r\n\r\n</div>\r\n</article></div>\r\n</div>\r\n</div>\r\n</div>\r\n<div class=\"right-column column\"></div>', 'PlayStation VR', '<div class=\"inner-top-step textGrey\"></div>\r\n<div class=\"inner-bottom-step textGrey\"></div>\r\n<hgroup>\r\n<h2 class=\"\">Powered by PS4</h2>\r\n</hgroup>\r\n<div class=\"inner-markup \">\r\n\r\nPlayStation VR is the latest member of the PS4 family – so whichever PS4 console you own, you’re PS VR ready; connect the headset to your PS4, add a PlayStation Camera* and leave reality behind.\r\n\r\n</div>', 'publish', 'open', 'closed', '', 'playstation-vr', '', '', '2018-04-22 19:54:18', '2018-04-22 19:54:18', '', 0, 'http://localhost/FatherOfGaming/?post_type=product&#038;p=2310', 0, 'product', '', 0),
